import React, {useState} from 'react'
import './main.css';

export default function Index() {
    const [product , setProduct] = useState("");
    const [additem , setItem] = useState([]);

    const addItem = () => {
        if(!product){

        }
        else{
            setItem([...additem, product]);
            setProduct('');
        }
    }

    const deleteItem = (id) => {
        console.log(id);
        const deleteItemm = additem.filter((pro,index) => {
           return !index == id;
        });
        setItem(deleteItemm);
    }
  return (
    <>
    <div className='outer-box'>
      <div className='container'>
        <div className='row'>
          <div className='col-md-6 col-sm-12 col-lg-6'>
            <div className='inner-box mb-auto'>
                <div className='row'>
                    <div className='col-lg-10 col-md-10 col-sm-8'>
                    <input className="form-control mb-3 shadow" id="username" type="text" 
                      name="name" value={product} onChange={(e) => setProduct(e.target.value)}
                    placeholder="Enter Your Product" aria-label=".form-control example" />
                    </div>
                    <div className='col-lg-2 col-md-2 col-sm-4'>
                    <button type="button" className="btn btn-warning shadow text-white" data-bs-toggle="tooltip" data-bs-placement="right" title="Add Your Product"
                       onClick={addItem}
                    ><i className="fa fa-plus" aria-hidden="true" title="Add Your Product"></i></button>
                    </div>
                    </div>
                    <div className='row'>
                        <div className='col-md-12'>
                           {/* <div className='row'>
                            <div className='col-md-12'> */}

                           { additem.map((pro,index) => {
                                return(

                                   <h6 className='text-white' key={index}>{index+1}. {pro}
                                    <div className='right-icon'>
                                        <i className="fa fa-pencil text-white" aria-hidden="true"></i>
                                        <i className="fa fa-trash text-white" aria-hidden="true" onClick={() => deleteItem(index)}></i>
                                        </div>
                                   </h6>
                                 
                                )
                            })
                        }
                                
                            {/* </div> */}
                            {/* <div className='col-md-2'>
                                <i className="fa fa-pencil text-white" aria-hidden="true"></i>
                            </div>
                            <div className='col-md-2'>
                                <i className="fa fa-trash text-white" aria-hidden="true"></i>
                            </div> */}
                           {/* </div> */}
                        </div>
                    </div>
                </div>
          </div>
            <div className='col-md-6 col-sm-12 col-lg-6'>
                <h1>Deepika</h1>
            </div> 
             </div>
         </div>
      </div>
    </>
  )
}
