  return (
    <div>
          
    <div className="container">
         <h1 className="text-center m-5 fw-bold">Todo App</h1>
         <form>
        <div className="row">
          <div className="col-md-10">

            <input className="form-control form-control-lg mb-3 shadow" id="username" type="text" 
              name="name" value={username} onChange={(e) => setState(e.target.value)}
            placeholder="Enter Your Name" aria-label=".form-control-lg example" />

          </div>
          <div className="col-md-2">
           <button type="button" className="btn btn-warning shadow btn-lg text-white" 
            onClick={handleClick}>Add Name</button>
          </div>
        </div>
        </form>
      
        <ul>
          <li>
          <div className='row'>
            <div className='col-md-3'>
                <h5>Sr.No</h5>
              </div>
              <div className='col-md-3'>
                <h5>Name</h5>
              </div>
              <div className='col-md-3'>
                <h5>Update</h5>
              </div>
              <div className='col-md-3'>
               <h5>Delete</h5>
              </div>
            </div>
            <hr />
          </li>

          <TodolistDelete />

        {/* {todos.map((todo, index) => (
          <li key={index}>
            <div className='row mb-3'>
            <div className='col-md-3'>
                 {index+1}
              </div>
              <div className='col-md-3'>
                 {todo}
              </div>
              <div className='col-md-3'>
                 <button type="button" className="btn btn-warning shadow"><i className="fa fa-pencil text-white" aria-hidden="true"></i></button>
              </div>
              <div className='col-md-3'>
              <button className="btn btn-danger shadow" onClick={() => deleteTodo(index)}><i className="fa fa-trash text-white" aria-hidden="true"></i></button>
              </div>
            </div>  
          </li>
        ))} */}
      </ul>

      
    </div>
   
    </div>
  )
}

