import React, {useState} from 'react'
import './main.css';

export default function Index() {
    const [product , setProduct] = useState("Add");
    const [additem , setItem] = useState([]);

    const addItem = () => {
        if(!product){

        }
        else{
            setItem([...additem, product]);
            setProduct('');
        }
        
       

    }
  return (
    <>
    <div className='outer-box'>
      <div className='container'>
        <div className='row'>
          <div className='col-md-6 col-sm-12 col-lg-6'>
            <div className='inner-box mb-auto'>
                <div className='row pt-3'>
                    <div className='col-md-8 col-sm-8 col-lg-8'>
                    <input className="form-control mb-3 shadow" id="username" type="text" 
                      name="name" value={product} onChange={(e) => setProduct(e.target.value)}
                    placeholder="Enter Your Product" aria-label=".form-control example" />
                    </div>
                    <div className='col-lg-4 col-md-4 col-4'>
                    <button type="button" className="btn btn-warning shadow text-white" data-bs-toggle="tooltip" data-bs-placement="right" title="Add Your Product"
                       onClick={addItem}
                    ><i className="fa fa-plus" aria-hidden="true" title="Add Your Product"></i></button>
                    </div>
                    </div>
                    <div className='row p-3'>
                        <div className='col-md-8'>
                           <h4>Deepika</h4>
                        </div>
                    </div>
                </div>
          </div>
            <div className='col-md-6 col-sm-12 col-lg-6'>
                <h1> {product}</h1>
            </div> 
             </div>
         </div>
      </div>
    </>
  )
}
