 import React, {useState} from 'react';


function Todolist() {
    const[username, setState] = useState("");
    const[items,setItem] = useState([]);
    // const handleChange = (e) =>{
    //     console.log(e.target.value);
    //     setState(e.target.value);
    // }
   
    const handleClick = (oldItems) => {
       setItem((oldItems) => {
        return [...oldItems, username]
       });      
    };
  return (
    <div>
          
    <div className="container">
         <h1 className="text-center m-5 fw-bold">Todo App</h1>
         <form>
        <div className="row">
          <div className="col-md-10">

            <input className="form-control form-control-lg mb-3 shadow" id="username" type="text" 
              name="name" value={username} onChange={(e) => setState(e.target.value)}
            placeholder="Enter Your Name" aria-label=".form-control-lg example" />

          </div>
          <div className="col-md-2">
           <button type="button" className="btn btn-warning shadow btn-lg text-white" 
            onClick={handleClick}>Add Name</button>
          </div>
        </div>
        </form>
      <div className="row">
        <div className="col-md-12">
          <table className="table table-striped table-hover shadow border text-center">
            <thead className="table-dark">
              <tr>
                <th scope="col">Sr.No</th>
                <th scope="col">Your Name</th>
         
                <th>Action</th>
    
              </tr>
            </thead>
            <tbody id="records">
            {items.map((itemval) => {
                  return(
                    <tr>
                    <th scope="row"></th>
                    <td>{itemval}</td>
                    <td> <button type="button" className="btn btn-warning shadow"><i className="fa fa-pencil text-white" aria-hidden="true"></i></button>
                    <button type="button" className="btn btn-danger shadow"><i className="fa fa-trash text-white" aria-hidden="true"></i></button></td>
                  </tr>

                  );
                 
                })}
              
          
             
            </tbody>
          </table>
        </div>
      </div>
    </div>
   
    </div>
  )
}

export default Todolist
