import React from 'react';
import {Link, useSearchParams} from 'react-router-dom';
import Todolist from './Todolist';

export default function Navbar() {

  // ----------------query string search Params -----------------
  const [searchParams, setSearchParams] = useSearchParams();

  const handleSearch = (e) => {
    e.preventDefault();
    const data = searchParams.get('q');
    alert(data);
  }
  return (
    <>
  <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
  <div className="container-fluid">
    {/* <Link className="navbar-brand" to="#">MyTask</Link> */}
    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span className="navbar-toggler-icon"></span>
    </button>
    <div className="collapse navbar-collapse" id="navbarSupportedContent">
      <ul className="navbar-nav me-auto mb-2 mb-lg-0">
        <li className="nav-item">
          <Link className="nav-link fw-bold" aria-current="page" to='/ToDo'>TodoList</Link>
        </li>
        <li className="nav-item">
          <Link className="nav-link fw-bold" to='/Login'>Login</Link>
        </li>
        <li className="nav-item">
          <Link className="nav-link fw-bold" to="#">Register</Link>
        </li>
      
      </ul>
      <form className="d-flex" onSubmit={handleSearch}>
        <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search"
         onChange={(e) => setSearchParams({q:e.target.value})}/>
        <button className="btn btn-outline-warning" type="submit">Search</button>
      </form>

    </div>
  </div>
</nav>

<Todolist/>
    </>
  )
}
